import {QuizRound} from "src/app/model/QuizRoundModel";
import {DTO} from "./typ";

export const convertToDTOProject = (quizRound: QuizRound): DTO<QuizRound> => {
  return {
    question: quizRound.question,
    choices: quizRound.choices,
    rightAnswer: quizRound.rightAnswer,
    key: quizRound.key,
  };
};

export const convertToProject = (projectDTO: QuizRound): QuizRound => {
  return new QuizRound({
    question: String(projectDTO.question),
    choices: String(projectDTO.choices),
    rightAnswer: String(projectDTO.rightAnswer),
    key: String(projectDTO.key),
  });
};
