// import {request} from "src/app/service/request";
// import {FetchMethod} from "./enu";

export enum FetchMethod {
  GET = "GET",
  POST = "POST",
  PUT = "PUT",
  DELETE = "DELETE",
}

export const request = async (url: string, method: FetchMethod, body?: QuizRound): Promise<FetchMethod> => {
  const stringifiedBody = JSON.stringify(body);
  const rawResponse = await fetch(url, {
    method,
    body: stringifiedBody,
  });

  // обработка ответа
  if (rawResponse.ok) {
    return method !== FetchMethod.DELETE
      ? rawResponse.json()
      : undefined;
  }

  throw new Error(String(rawResponse.status));
};

export const fetchRequest = {

  /**
	 * Создание get запроса
	 */
  get: async <T>(url: string): Promise<T> => {return request(url, FetchMethod.GET);},

};
