import {QuizRound} from "src/app/model/QuizRoundModel";
// import { questionsRoundDTO } from "./IQuestionsDTO";

export type DTO<T> = Record<keyof T, unknown>

export const convertToDTOProject = (quizRound: QuizRound): DTO<QuizRound> => {
  return {
    question: quizRound.question,
    choices: quizRound.choices,
    rightAnswer: quizRound.rightAnswer,
    key: quizRound.key,
  };
};

/**
 * Class which use when the client have internet
 */
export class QuizService {

  private baseUrl: string;

  constructor(baseUrl: string) {
    this.baseUrl = baseUrl;
  }

  /**
	 * Method which return data
	 */
  public async getQuestions(): Promise<QuizRound[]> {
    const url = this.baseUrl;
    const {content: questionsRoundDTO} = await fetchRequest.get(url);

    const questions = questionsRoundDTO.map(convertToDTOProject);

    return questions;
  }

}

enum FetchMethod {
  GET = "GET",
  POST = "POST",
  PUT = "PUT",
  DELETE = "DELETE",
}

const request = async (url: string, method: FetchMethod, body?: unknown | undefined) => {
  const stringifiedBody = JSON.stringify(body);
  const rawResponse = await fetch(url, {
    method,
    headers: {
      accept: "application/json",
      "Content-Type": "application/json",
    },
    body: stringifiedBody,
  });

  // обработка ответа
  if (rawResponse.ok) {
    return method !== FetchMethod.DELETE
      ? await rawResponse.json()
      : undefined;
  }

  throw new Error(String(rawResponse.status));
};
