import {QuizRound} from "../model/QuizRoundModel";
import {FetchMethod} from "./enu";

export const request = async (url: string, method: FetchMethod, body?: QuizRound): Promise<any> => {
  const stringifiedBody = JSON.stringify(body);
  const rawResponse = await fetch(url, {
    method,
    b//ody: stringifiedBody,
  });

  // обработка ответа
  if (rawResponse.ok) {
    return method !== FetchMethod.DELETE
      ? rawResponse.json()
      : undefined;
  }

  throw new Error(String(rawResponse.status));
};
