import React from "react";
import {Button} from "src/app/component/button/Button";
import {Paragraph, PARAGRAPH_STYLES} from "src/app/component/paragraph/Paragraph";
import {useGetQuestions} from "src/app/component/questions/UseGetQuestions";
import {Headline} from "../headline/Headline";

/**
 * Component for displaying quiz questions and answer options
 */
export const Questions: React.FC = () => {

  // Call custom hook
  const [score, questionIndex, answer, questions, methods] = useGetQuestions();

  const renderAnswerElement = (choice: string, index: number): any => {
    return (
      <h5 key={choice}>
        <label>
          <input
            type="radio"
            name="choice"
            value={choice}
            onChange={methods.onChange}
            checked={answer === choice}
            key={index}
          />
          <span className={PARAGRAPH_STYLES}>{choice}</span>
        </label>
      </h5>
    );
  };

  const renderAnswers = (): any => {
    return questions[questionIndex]?.choices.map(renderAnswerElement);
  };

  const isLastQuestion = questionIndex < questions.length;

  const getйByQuestionIndex = (index: number, questionsList: QuizRoundModel[]): string => {
    const question: QuizRoundModel | undefined = questionsList[index];
    if (!question) {
      throw new Error("wrong index in array");
    }
    return question.rightAnswer;
  };

  if (isLastQuestion) {
    return (
      <>
        <Headline text={questions[questionIndex].question} />
        {renderAnswers()}
        <Button title="Check" handleClick={methods.submit} />
        <Paragraph text={`score: ${score}`} />
      </>
    );
  }

  return (
    <>
      <Button title="Restart" handleClick={methods.restart} />
      <Paragraph text={`score: ${score}`} />
    </>
  );
};
