/**
 * Methods request
 */
export enum FetchMethod {
  GET = "GET",
  DELETE = "DELETE",
}

type request <T> = (e: React.ChangeEvent<T>) => void;

/**
 * Request
 * @param url path to database with data for quiz
 * @param method GET or DELETE
 * @returns response with data (quiz questions, answers, etc) or Error
 */
export const request = async (url: string, method: FetchMethod): Promise<T> => {
  const data = await fetch(url, {method});
  return data;
};

/**
 * Types of requests
 */
export const fetchRequest = {

  /**
   * Create get request
   */
  get: async <T>(url: string): Promise<T> => { return request(url, FetchMethod.GET); },

};
